import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import './Profile.css';
import { connect } from 'react-redux';
import DragAndDrop from './DragAndDrop'
import { DragAndDropReducer } from '../../redux/reducers/draganddrop/DragAndDrop'
import store from '../../redux/store/store'

const PostBlock = (props) => {
  //для редакса

  const [postDate, setPostDate] = useState("");
  const [postHeadline, setPostHeadline] = useState("");
  const [postText, setPostText] = useState("");
  const [postTime, setPostTime] = useState("");

  return (
    <div className="col-4 post-block">
        <div className="profile-block d-flex justify-content-end align-items-center">
            <div className="profile-name">{props.name.name}</div>
            <div className="profile-photo d-flex justify-content-center align-items-center">
                <img className="profile-img" src="https://7kingdoms.ru/wp-content/uploads/2019/01/cerceiin_black-300x450.jpg"></img>
            </div>
            <div>
                &#9207;
                <div className="profile-menu d-none">
                    <div>Settings</div>
                    <div>Log Out</div>
                </div>
            </div>
        </div>
        <div className="post-block-headline">
            Agenda
        </div>

    </div>
  );
}

const mapStateToProps = state => {
    return {
        name: state.userReducer
    };
  };

//const mapDispatchToProps = {
//  logout
//}

export default connect(
  mapStateToProps,
  null
)(PostBlock)
