import { typesPosts } from '../../actions/posts/posts'

const initialState = {
    email: 'null',
    postDate: 'null',
    postHeadline: 'null',
    postText: 'null',
    postTime: 'null',
    posts: 'null',
    id: 'null',
    index: 'null'
}

export function postsReducer(state = initialState, action = {}) {
  switch (action.type) {
    case typesPosts.POST.PUSH:
      return {   
        ...state,
        email: action.email,
        postHeadline: action.postHeadline,
        postText: action.postText,
      }
    case typesPosts.POST.GET:
      return {   
        email: action.email,
        ...state,
      }
    case typesPosts.POST.GET_SUCCESS:
    return {   
        ...state,
        posts: action.posts
    }
    case typesPosts.POST.REMOVE:
        return {   
            ...state,
            id: action.id,
            email: action.email,
            index: action.index
        }
    default:
      return state
  }
}
