import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import './Profile.css';
import { connect } from 'react-redux';
import DragAndDrop from './DragAndDrop'
import { DragAndDropReducer } from '../../redux/reducers/draganddrop/DragAndDrop'
import store from '../../redux/store/store'
import database from '../../rsf/rsf' 

const PostBlock = (props) => {
  
    useEffect( () => {
        console.log(database().ref(`users/`))
//        database.ref(`${props.user.uid}`).on("value", snapshot => {
//         let allItems =  [];
//          snapshot.forEach(snap => {
//           allItems.push(snap.val());
//         });
//         setForMap(allItems)
     });  

    return (
        <div className="col-4 post-block">
            <div className="profile-block d-flex justify-content-end align-items-center">
                <div className="profile-name">{props.name.name}</div>
                <div className="profile-photo d-flex justify-content-center align-items-center">
                    <img className="profile-img" src="https://static.espreso.tv/uploads/article/2603475/images/im578x383-009-9synh.jpg"></img>
                </div>
                <div>
                    &#9207;
                    <div className="profile-menu d-none">
                        <div>Settings</div>
                        <div>Log Out</div>
                    </div>
                </div>
            </div>
            <div className="post-block-headline">
                Agenda
            </div>

        </div>
    );
}

const mapStateToProps = state => {
    return {
        name: state.userReducer
    };
  };

//const mapDispatchToProps = {
//  logout
//}

export default connect(
  mapStateToProps,
  null
)(PostBlock)
