import { typesPosts } from '../../actions/posts/posts'

const initialState = {
    email: 'null',
    postDate: 'null',
    postHeadline: 'null',
    postText: 'null',
    postTime: 'null',
}

export function postsReducer(state = initialState, action = {}) {
  switch (action.type) {
    case typesPosts.POST.PUSH:
      return {   
        ...state,
        email: action.email,
        postDate: action.data.postDate,
        postHeadline: action.data.postHeadline,
        postText: action.data.postText,
        postTime: action.data.postTime,

      }
    case typesPosts.POST.GET:
      return {   
        ...state,
        email: action.email,
        postDate: action.data.postDate,
        postHeadline: action.data.postHeadline,
        postText: action.data.postText,
        postTime: action.data.postTime,
      }
    default:
      return state
  }
}
