import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import './Profile.css';
import { connect } from 'react-redux';
import DragAndDrop from './DragAndDrop'
import { DragAndDropReducer } from '../../redux/reducers/draganddrop/DragAndDrop'
import store from '../../redux/store/store'
import rsf from '../../rsf/rsf' 
import { postGet } from '../../redux/actions/posts/posts'

const PostBlock = (props) => {
    const posts = Object.values(props.posts);
    const posts1 = Object.values(posts[0]);
    console.log(typeof(posts1))
    const posts2 = [];

    for (const item of posts1) {
        posts2.push(item)
        console.log(posts2)
    }

    return (
        <div className="col-4 post-block">
            <div className="profile-block d-flex justify-content-end align-items-center">
                <div className="profile-name">{props.name}</div>
                <div className="profile-photo d-flex justify-content-center align-items-center">
                    <img className="profile-img" src="https://static.espreso.tv/uploads/article/2603475/images/im578x383-009-9synh.jpg"></img>
                </div>
                <div>
                    &#9207;
                    <div className="profile-menu d-none">
                        <div>Settings</div>
                        <div>Log Out</div>
                    </div>
                </div>
            </div>
            <div className="post-block-headline">
                Agenda
            </div>


            {/* email: 'null',
    postDate: 'null',
    postHeadline: 'null',
    postText: 'null',
    postTime: 'null',
    posts: 'null' */}


            {posts2.map((item, index) => (
                <div
                  key={item.key}
                  className=""
                >
                    <div className="postDate">
                        {item.postDate}
                    </div>
                    <div className="post-card">
                        <div>
                            <div className="post-headline d-flex justify-content-between">
                                {item.postHeadline}
                            </div>
                            <div className="post-remover">
                                Remover
                            </div>
                        </div>

                    </div>
                </div>
              ))}


        </div>
    );
}

const mapStateToProps = state => {
    return {
        name: state.userReducer.name,
        email: state.userReducer.email,
        posts: state.postsReducer.posts
    };
  };

const mapDispatchToProps = {
    postGet
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(PostBlock)
