import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import './Profile.css';
import { connect } from 'react-redux';
import DragAndDrop from './DragAndDrop'
import { DragAndDropReducer } from '../../redux/reducers/draganddrop/DragAndDrop'
import store from '../../redux/store/store'
import rsf from '../../rsf/rsf' 
import { postGet, postRemove } from '../../redux/actions/posts/posts'

const PostBlock = (props) => {
        const posts = Object.values(props.posts);
        console.log(posts)
        const posts1 = Object.values(posts[0]);
        console.log(Object.values(posts))
        const posts2 = [];

        for (const item of posts1) {
            posts2.push(item)
            console.log(posts2)
        }


    return (
        <div className="col-4 post-block">
            <div className="profile-block d-flex justify-content-end align-items-center">
                <div className="profile-name">{props.name}</div>
                <div className="profile-photo d-flex justify-content-center align-items-center">
                    <img className="profile-img" src="https://static.espreso.tv/uploads/article/2603475/images/im578x383-009-9synh.jpg"></img>
                </div>
                <div>
                    <i className="fas fa-sort-up"></i>
                    <div className="profile-menu d-none">
                        <div>Settings</div>
                        <div>Log Out</div>
                    </div>
                </div>
            </div>
            <div className="post-block-headline">
                Agenda
            </div>


            {posts2.map((item, index) => (
                <div
                  key={item.key}
                  className=""
                >
                    <div className="postDate">
                        {item.postDate}
                    </div>
                    <div className="post-card">
                        <div className="post-top d-flex justify-content-between align-items-center">
                            <div className="post-headline">
                                {item.postHeadline}
                            </div>
                            <div className="post-remover">
                                <i className="fas fa-trash" onClick={() => props.postRemove(props.email, index)}></i>
                            </div>
                        </div>
                        <div className="postText">
                            {item.postText}
                        </div>
                        <div className="post-bottom d-flex justify-content-between align-items-center">
                            <div className="social-links d-flex align-items-center">
                                <span className="post-bottom-text mr-1">socials:</span>
                                <i className="fab fa-facebook mr-1"></i>
                                <i className="fab fa-telegram mr-1"></i>
                                <i className="fab fa-vk"></i>
                            </div>
                            <div className="postTime">
                                <span className="post-bottom-text">Post time:</span> {item.postTime}
                            </div>


                        </div>

                    </div>
                </div>
              ))}


        </div>
    );
}

const mapStateToProps = state => {
    return {
        name: state.userReducer.name,
        email: state.userReducer.email,
        posts: state.postsReducer.posts
    };
  };

const mapDispatchToProps = {
    postGet,
    postRemove
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(PostBlock)
