import { call, put } from 'redux-saga/effects'
import { loginSuccess, loginFailure } from '../../actions/autorisation/Login'
import { types } from '../../actions/autorisation/Types'
import { typesUser } from '../../actions/user/Username'
import rsf from '../../../rsf/rsf'
import { myFirebaseApp } from '../../../rsf/rsf'

export function* createPostSaga({email, postDate, postHeadline, postText, postTime}) {
//  yield put({type: types.LOGIN.REQUEST})
  try {
    console.log('IM TRYING')
    myFirebaseApp.database().ref(`users/${email}`).push({
      postDate: postDate,
      postHeadline: postHeadline,
      postText: postText,
      postTime: postTime
    });
//    yield put(loginSuccess(user));
  }
  catch(error) {
    yield put(loginFailure(error));
  }
}
